<script>
	import {pairs} from 'lamb';
	import {isArray, isObject} from '@svizzle/utils';
	import ExternalLink from '@svizzle/ui/src/ExternalLink.svelte';

	export let item = null;
	export let text = null;
	export let title = null;
</script>

<div>
	{#if isObject(item)}
		<p>
			<b>{title}:</b>
		</p>
		<ul>
			{#each pairs(item) as [key, value]}
				{#if isArray(value)}
				<li>{key}:
					<ul>
						{#each value as href, index}
							<li>
								<span><ExternalLink {href} text='Link {index + 1}:'/></span>
							</li>
						{/each}
					</ul>
				</li>
				{:else}
					<li>
						<span><ExternalLink href={value} text='{key}:' /></span>
					</li>
				{/if}
			{/each}

		</ul>
	{:else if isArray(item)}
		<p>
			<b>{title}:</b>
		</p>
		<ul>
			{#each item as href, index}
			<li>
				<span><ExternalLink {href} text='Link {index + 1}:'/></span>
			</li>
			{/each}
		</ul>
	{:else}
		<p>
			<b>{title}:</b>
			<ExternalLink href={item} {text} />
		</p>
	{/if}
</div>

<style>
	b {
		font-family: var(--fontFamilyRegular), sans-serif;
		margin-right: 0.25rem;
	}

	ul {
		margin-left: 1.5rem;
	}
</style>
